const logoVariants = cva(
  // Base styles
  "inline-flex items-center",
  {
    variants: {
      size: {
        default: "gap-3", // 12px gap from Figma
        sm: "gap-2",
        lg: "gap-4",
      },
    },
    defaultVariants: {
      size: "default",
    },
  }
)

const logoTextVariants = cva(
  // Base text styles - uses brand-text token
  "text-[var(--semantic-brand-text)] whitespace-nowrap",
  {
    variants: {
      size: {
        default: "text-base leading-6", // 16px, 24px line-height from Figma
        sm: "text-sm leading-5", // 14px, 20px line-height
        lg: "text-xl leading-8", // 20px, 32px line-height - larger to match lg logo
      },
    },
    defaultVariants: {
      size: "default",
    },
  }
)

const logoSvgVariants = cva(
  // Base SVG styles
  "shrink-0",
  {
    variants: {
      size: {
        default: "h-6 w-auto", // 24px height from Figma
        sm: "h-5 w-auto",
        lg: "h-8 w-auto",
      },
    },
    defaultVariants: {
      size: "default",
    },
  }
)

/**
 * Logo component props interface
 * 
 * @example
 * ```tsx
 * // Logo only
 * <Logo />
 * 
 * // Logo with product name
 * <Logo productName="Product Name" />
 * 
 * // Different sizes
 * <Logo size="sm" productName="Small" />
 * <Logo size="lg" productName="Large" />
 * ```
 */
export interface LogoProps
  extends Omit<React.ComponentProps<"div">, "children">,
    VariantProps<typeof logoVariants> {
  /**
   * Optional product name to display next to the logo
   */
  productName?: string
  /**
   * Show only the logo without the product name
   * @default false
   */
  logoOnly?: boolean
}

/**
 * Logo component - JLL brand logo with optional product name
 * 
 * Uses semantic brand tokens for theming:
 * - `--semantic-brand-picto`: Red "J" icon (constant across themes)
 * - `--semantic-brand-text`: "LL" text and product name (black/white based on theme)
 * 
 * @param props - Logo props including productName, size, and standard HTML div attributes
 * @returns A logo element with appropriate styling
 * 
 * @example
 * ```tsx
 * // Basic usage
 * <Logo productName="My Product" />
 * 
 * // Logo only (no product name)
 * <Logo logoOnly />
 * 
 * // With custom className
 * <Logo productName="Product" className="my-4" />
 * ```
 */
function Logo({
  className,
  size,
  productName,
  logoOnly = false,
  ...props
}: LogoProps): React.ReactElement {
  return (
    <div
      data-slot="logo"
      className={cn(logoVariants({ size }), className)}
      {...props}
    >
      <JLLLogo className={logoSvgVariants({ size })} />
      {!logoOnly && productName && (
        <span
          data-slot="logo-text"
          className={logoTextVariants({ size })}
        >
          {productName}
        </span>
      )}
    </div>
  )
}

export { Logo, JLLLogo, logoVariants }

